void Intake::Configure()
{
	if (myConfig->Intake_intakeTalon1_Enabled)
	{
		printf("I:Configure IT1 enabled\n");
        if (myConfig->Intake_intakeTalon1_HasSensor)
        {
        	intakeTalon1->SetFeedbackDevice(CANTalon::QuadEncoder);
			if (myConfig->Intake_intakeTalon1_SensorReversed)
			{
				intakeTalon1->SetSensorDirection(true);
			} else
			{
				intakeTalon1->SetSensorDirection(false);
			}
        }
        if (myConfig->Intake_intakeTalon1_EnablePID)
        {
        	printf("I:Configure IT1 PID enabled\n");
        	intakeTalon1->SelectProfileSlot(0);
        	intakeTalon1->SetP(myConfig->Intake_intakeTalon1_Profile_0_PID_P);
        	intakeTalon1->SetI(myConfig->Intake_intakeTalon1_Profile_0_PID_I);
        	intakeTalon1->SetD(myConfig->Intake_intakeTalon1_Profile_0_PID_D);
        	intakeTalon1->SetF(myConfig->Intake_intakeTalon1_Profile_0_PID_F);
			if (myConfig->Intake_intakeTalon1_Profile_0_EnableCLRampRate)
			{
				intakeTalon1->SetCloseLoopRampRate(myConfig->Intake_intakeTalon1_Profile_0_CLRampRate);
			}

			intakeTalon1->SetIzone(myConfig->Intake_intakeTalon1_Profile_0_IZone);

			intakeTalon1->SelectProfileSlot(1);
			intakeTalon1->SetP(myConfig->Intake_intakeTalon1_Profile_1_PID_P);
			intakeTalon1->SetI(myConfig->Intake_intakeTalon1_Profile_1_PID_I);
			intakeTalon1->SetD(myConfig->Intake_intakeTalon1_Profile_1_PID_D);
			intakeTalon1->SetF(myConfig->Intake_intakeTalon1_Profile_1_PID_F);
			if (myConfig->Intake_intakeTalon1_Profile_1_EnableCLRampRate)
			{
				intakeTalon1->SetCloseLoopRampRate(myConfig->Intake_intakeTalon1_Profile_1_CLRampRate);
			}

			intakeTalon1->SetIzone(myConfig->Intake_intakeTalon1_Profile_1_IZone);
        }

		if(myConfig->Intake_intakeTalon1_EnableVoltRampRate)
		{
			intakeTalon1->SetVoltageRampRate(myConfig->Intake_intakeTalon1_VoltRampRate);
		}

		if(myConfig->Intake_intakeTalon1_Slaved)
		{
			intakeTalon1->SetControlMode(CANSpeedController::kFollower);
			intakeTalon1->Set(myConfig->Intake_intakeTalon1_MasterCANID);
		}
	}

	if (myConfig->Intake_pivotTalon_Enabled)
	{
		printf("I:Configure PT enabled\n");
        if (myConfig->Intake_pivotTalon_HasSensor)
        {
        	pivotTalon->SetFeedbackDevice(CANTalon::QuadEncoder);
			if (myConfig->Intake_pivotTalon_SensorReversed)
			{
				pivotTalon->SetSensorDirection(true);
			} else
			{
				pivotTalon->SetSensorDirection(false);
			}
        }
        if (myConfig->Intake_pivotTalon_EnablePID)
        {
        	printf("I:Configure PT PID enabled\n");
        	pivotTalon->SelectProfileSlot(0);
        	pivotTalon->SetP(myConfig->Intake_pivotTalon_Profile_0_PID_P);
        	pivotTalon->SetI(myConfig->Intake_pivotTalon_Profile_0_PID_I);
        	pivotTalon->SetD(myConfig->Intake_pivotTalon_Profile_0_PID_D);
        	pivotTalon->SetF(myConfig->Intake_pivotTalon_Profile_0_PID_F);
			if (myConfig->Intake_pivotTalon_Profile_0_EnableCLRampRate)
			{
				pivotTalon->SetCloseLoopRampRate(myConfig->Intake_pivotTalon_Profile_0_CLRampRate);
			}

			pivotTalon->SetIzone(myConfig->Intake_pivotTalon_Profile_0_IZone);

			pivotTalon->SelectProfileSlot(1);
			pivotTalon->SetP(myConfig->Intake_pivotTalon_Profile_1_PID_P);
			pivotTalon->SetI(myConfig->Intake_pivotTalon_Profile_1_PID_I);
			pivotTalon->SetD(myConfig->Intake_pivotTalon_Profile_1_PID_D);
			pivotTalon->SetF(myConfig->Intake_pivotTalon_Profile_1_PID_F);
			if (myConfig->Intake_pivotTalon_Profile_1_EnableCLRampRate)
			{
				pivotTalon->SetCloseLoopRampRate(myConfig->Intake_pivotTalon_Profile_1_CLRampRate);
			}

			pivotTalon->SetIzone(myConfig->Intake_pivotTalon_Profile_1_IZone);
        }

		if(myConfig->Intake_pivotTalon_EnableVoltRampRate)
		{
			pivotTalon->SetVoltageRampRate(myConfig->Intake_pivotTalon_VoltRampRate);
		}
		if(myConfig->Intake_pivotTalon_Slaved)
		{
			pivotTalon->SetControlMode(CANSpeedController::kFollower);
			pivotTalon->Set(myConfig->Intake_pivotTalon_MasterCANID);
		}
	}
	pivotTalon->SetControlMode(CANSpeedController::kPosition);
}

