void Intake::SetIntake(bool in)
{
	if (in)
	{
		intakeTalon1->Set(myConfig->Intake_InSpeed);
	} else
	{
		intakeTalon1->Set(myConfig->Intake_OutSpeed);
	}

}

void Intake::StopIntake()
{
	intakeTalon1->Set(0);
}

void Intake::SetPivotHome()
{
	home = pivotTalon->GetEncPosition();
	homeSet = true;
}

void Intake::SetPivotToHome()
{
	pivotTalon->SetControlMode(CANSpeedController::kPosition);
	if (homeSet)
	{
		pivotTalon->SetPosition((double) homeSet);
	}
}

void Intake::SetPivotIntake()
{
	pivotTalon->SetControlMode(CANSpeedController::kPosition);
	if (homeSet)
	{
		pivotTalon->SetPosition((double) (homeSet + myConfig->Intake_IntakeOffset));
	}
}

void Intake::SetPivotLow()
{
	pivotTalon->SetControlMode(CANSpeedController::kPosition);
	if (homeSet)
	{
		pivotTalon->SetPosition((double) (homeSet + myConfig->Intake_LowOffset));
	}
}

void Intake::SetPivot(float value)
{
	pivotTalon->SetControlMode(CANSpeedController::kPercentVbus);
	pivotTalon->Set(Limit(value));
}

float Intake::Limit(float num)
{
	if (num > 1.0)
	{
		return 1.0;
	}
	if (num < -1.0)
	{
		return -1.0;
	}
	return num;
}
